                                    	   @var_counter,
                                           @var_AccountSeek,
                                           '',
                                           0,
                                           @value_minux_tax);
                                           
                                         
                    		
                  
                
       		END IF;
    
    UNTIL done END REPEAT;
        
    CLOSE var_cashCursor;
    
    END IF;     		 
