        ON a.AccountID_cd = b.AccountID_cd

        LEFT JOIN tb_mcoadtl AS c
        ON a.SubsidiaryID_cd = c.SubsidiaryID_cd

        ORDER BY a.DocDate_dt ASC,
                 a.Journal_tx ASC,
                 a.Ref_tx ASC;
 


END $$
