BEGIN
        SET @mSql =
        CONCAT('SELECT a.PaymasterID_cd,
                       a.PaymasterName_tx,
                       a.TIN_tx,
                       a.Address_tx,
                       a.TelNo_tx,
                       a.Email_tx,
                       a.Type_tx,
