        LEFT JOIN  
        tb_mcoahdr as b
        ON a.AccountID_cd = b.AccountID_cd

        LEFT JOIN
        tb_mcoagroup as c
        ON b.GroupID_cd = c.GroupID_cd


        WHERE a.AccountID_cd <> ''

        GROUP BY a.AccountID_cd,
	         b.AccountDesc_tx,
	         b.Debit_yn

        ORDER BY b.AccountID_cd;
    
END $$
