set mDate2 =concat(mYear2,"/",mMonth2,"/",mDay2);
 
SET @mystatement =CONCAT('SELECT itemid_cd, itemname_tx,sum(TIn) as TIn, sum(TOut) as TOut, sum(MBalance) as MBalance from (',
