                 a.SubsidiaryID_cd) a

        LEFT JOIN
        (SELECT *
        FROM gng_master.tb_mcoadtl) b
        ON a.SubsidiaryID_cd = b.SubsidiaryID_cd

        WHERE (a.Journal_tx = mJournal_tx OR mJournal_tx = '0') AND
              a.SubsidiaryID_cd <> '';
